import React, { useState, useEffect, useCallback } from 'react';
import { useTranslation } from '../hooks/useTranslation';

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileNavActive, setMobileNavActive] = useState(false);
  const [activeSection, setActiveSection] = useState('hero');
  const { toggleLanguage } = useTranslation();

  // Scroll spy melhorado
  const updateActiveSection = useCallback(() => {
    const sections = [
      { id: 'hero', element: document.getElementById('hero') },
      { id: 'metodologias', element: document.getElementById('metodologias') },
      { id: 'service-details', element: document.getElementById('service-details') },
      { id: 'services', element: document.getElementById('services') },
      { id: 'portfolio', element: document.getElementById('portfolio') },
      { id: 'form-section', element: document.getElementById('form-section') }
    ].filter(section => section.element);

    if (sections.length === 0) return;

    const scrollPosition = window.scrollY + 120; // Offset para header

    // Encontra a seção ativa
    let currentActive = 'hero';
    
    for (const section of sections) {
      const { element, id } = section;
      const offsetTop = element.offsetTop;
      const offsetHeight = element.offsetHeight;
      
      if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
        currentActive = id;
        break;
      }
    }

    // Se nenhuma seção encontrada, usa a última que passou
    if (currentActive === 'hero' && scrollPosition > sections[0].element.offsetTop) {
      const passedSections = sections.filter(s => scrollPosition >= s.element.offsetTop);
      if (passedSections.length > 0) {
        currentActive = passedSections[passedSections.length - 1].id;
      }
    }

    setActiveSection(currentActive);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 100);
      updateActiveSection();
    };

    // Debounce para performance
    let ticking = false;
    const onScroll = () => {
      if (!ticking) {
        window.requestAnimationFrame(() => {
          handleScroll();
          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener('scroll', onScroll);
    updateActiveSection(); // Chama uma vez no carregamento

    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [updateActiveSection]);

  const handleNavClick = (sectionId) => {
    setMobileNavActive(false);
    document.body.classList.remove('mobile-nav-active');
    setActiveSection(sectionId);
  };

  const toggleMobileNav = () => {
    setMobileNavActive(!mobileNavActive);
    if (!mobileNavActive) {
      document.body.classList.add('mobile-nav-active');
    } else {
      document.body.classList.remove('mobile-nav-active');
    }
  };

  const navItems = [
    { href: '#hero', text: 'menu_about', icon: 'bi-person', id: 'hero' },
    { href: '#metodologias', text: 'menu_methodologies', icon: 'bi-diagram-3', id: 'metodologias' },
    { href: '#service-details', text: 'menu_certificates', icon: 'bi-award', id: 'service-details' },
    { href: '#services', text: 'menu_services', icon: 'bi-briefcase', id: 'services' },
    { href: '#portfolio', text: 'menu_projects', icon: 'bi-folder', id: 'portfolio' },
    { href: '#form-section', text: 'menu_contact', icon: 'bi-envelope', id: 'form-section' },
  ];

  return (
    <>
      <header 
        id="header" 
        className={`header d-flex align-items-center fixed-top ${scrolled ? 'scrolled' : ''}`}
      >      
        <div className="container position-relative d-flex align-items-center justify-content-between">
          <a href="/" className="logo d-flex align-items-center" onClick={() => handleNavClick('hero')}>
            <h1 className="sitename">Matheus Abib</h1><span>.</span>
          </a>

          <nav id="navmenu" className={`navmenu ${mobileNavActive ? 'mobile-nav-active' : ''}`}>
            <ul>
              {navItems.map((item) => (
                <li key={item.id}>
                  <a 
                    href={item.href}
                    data-translate={item.text}
                    onClick={(e) => {
                      e.preventDefault();
                      handleNavClick(item.id);
                      // Rola para a seção
                      const element = document.getElementById(item.id);
                      if (element) {
                        const headerHeight = document.querySelector('#header').offsetHeight;
                        const elementPosition = element.getBoundingClientRect().top;
                        const offsetPosition = elementPosition + window.pageYOffset - headerHeight;
                        
                        window.scrollTo({
                          top: offsetPosition,
                          behavior: 'smooth'
                        });
                      }
                    }}
                    className={`d-flex align-items-center ${activeSection === item.id ? 'active' : ''}`}
                  >
                    <i className={`${item.icon} me-2`}></i>
                    <span>{item.text}</span>
                    {activeSection === item.id && (
                      <span className="active-dot ms-2"></span>
                    )}
                  </a>
                </li>
              ))}
              <li className="d-block d-xl-none mt-3 pt-3 border-top">
                <button 
                  className="btn-translate w-100 d-flex justify-content-center align-items-center" 
                  onClick={() => {
                    toggleLanguage();
                    handleNavClick(activeSection);
                  }}
                >
                  <i className="bi bi-translate me-2"></i>
                  <span data-translate="btn_translate">PT/EN</span>
                </button>
              </li>
            </ul>
          </nav>

          <div className="d-none d-xl-block">
            <button 
              className="btn-translate" 
              id="translateBtnDesktop" 
              data-lang="pt"
              onClick={toggleLanguage}
            >
              <i className="bi bi-translate"></i>
              <span data-translate="btn_translate">PT/EN</span>
            </button>
          </div>

          <i 
            className={`mobile-nav-toggle d-xl-none bi ${mobileNavActive ? 'bi-x' : 'bi-list'}`}
            onClick={toggleMobileNav}
            aria-label="Toggle navigation"
          ></i>
        </div>
      </header>

      {mobileNavActive && (
        <div 
          className="mobile-nav-overlay"
          onClick={() => handleNavClick(activeSection)}
        ></div>
      )}
    </>
  );
};

export default Header;
