import React, { useState } from 'react';

const Certificates = () => {
  const [activeTab, setActiveTab] = useState('webdev');

  const certificates = [
    {
      id: 'webdev',
      title: 'HTML, CSS & JavaScript',
      institution: 'Coursera - Johns Hopkins University',
      image: '/assets/img/services/Web-Development.jpg',
      description: 'Curso especializado em desenvolvimento web front-end, abordando as tecnologias fundamentais para construção de interfaces modernas e responsivas.',
      skills: ['CSS', 'Bootstrap', 'JavaScript', 'HTML5', 'AJAX']
    },
    {
      id: 'powerbi',
      title: 'Microsoft Power BI',
      institution: 'Data Science Academy',
      image: '/assets/img/services/Power-BI.jpg',
      description: 'Curso completo de Microsoft Power BI para Business Intelligence e Data Science, abordando desde conceitos básicos até técnicas avançadas de análise e visualização de dados.',
      skills: ['Microsoft Power BI', 'Microsoft Excel', 'Dashboards', 'Análise de Dados', 'Gerenciamento de Planilhas']
    },
    {
      id: 'database',
      title: 'Banco de Dados SQL',
      institution: 'IFSul - Plataforma Mundi',
      image: '/assets/img/services/Banco-de-Dados.jpg',
      description: 'Curso completo de banco de dados relacional, abordando desde conceitos fundamentais até consultas avançadas em SQL, modelagem de dados e otimização de queries.',
      skills: ['Banco de Dados', 'SQL', 'Gerenciamento de Planilhas', 'Análise de Dados']
    },
    {
      id: 'logic',
      title: 'Lógica de Programação',
      institution: 'DIO - Digital Innovation One',
      image: '/assets/img/services/Logica-de-Programacao.jpg',
      description: 'Curso introdutório à lógica de programação através do programa GFT Start #6, focado em fundamentos de algoritmos e estruturas de dados utilizando JavaScript.',
      skills: ['JavaScript', 'Git', 'GitHub', 'Lógica']
    },
    {
      id: 'ai',
      title: 'Inteligência Artificial',
      institution: 'Alura - Imersão IA',
      image: '/assets/img/services/IA.jpg',
      description: 'Imersão em Inteligência Artificial 2ª edição, abordando desde conceitos fundamentais até técnicas avançadas de machine learning e deep learning com Python.',
      skills: ['Fundamentos de IA', 'Aprendizado Supervisionado', 'Pré-processamento de Dados', 'Python']
    },
    {
      id: 'java',
      title: 'Java & Spring Boot',
      institution: 'Especialização em Java',
      image: '/assets/img/services/Java.png',
      description: 'Minicurso especializado em Java moderno e Spring Boot, abordando desde fundamentos da linguagem até desenvolvimento de APIs RESTful com boas práticas e padrões de projeto.',
      skills: ['Java', 'Spring Boot', 'API Rest', 'Database', 'Lombok']
    }
  ];

  const tabs = [
    { id: 'webdev', label: 'Web Development' },
    { id: 'powerbi', label: 'Power BI' },
    { id: 'database', label: 'Banco de Dados' },
    { id: 'logic', label: 'Lógica de Programação' },
    { id: 'ai', label: 'Inteligência Artificial' },
    { id: 'java', label: 'Java' }
  ];

  const activeCertificate = certificates.find(cert => cert.id === activeTab);

  return (
    <section id="service-details" className="service-details section">
      <div className="container section-title" data-aos="fade-up">
        <h2 data-translate="menu_certificates">Certificados</h2>
      </div>
      
      <div className="container" data-aos="fade-up" data-aos-delay="100">
        <div className="certificates-wrapper">
          <div className="certificates-tabs-modern">
            <div className="certificates-nav">
              {tabs.map(tab => (
                <button
                  key={tab.id}
                  className={`certificate-tab-btn ${activeTab === tab.id ? 'active' : ''}`}
                  onClick={() => setActiveTab(tab.id)}
                  data-tab={tab.id}
                >
                  <span data-translate={`${tab.id}_tab_title`}>{tab.label}</span>
                </button>
              ))}
            </div>
            
            <div className="certificates-content">
              {activeCertificate && (
                <div className="certificate-tab active" id={`${activeTab}-tab`}>
                  <div className="certificate-card">
                    <div className="certificate-image">
                      <img src={activeCertificate.image} alt={`Certificado ${activeCertificate.title}`} />
                      <div className="certificate-badge">
                        <i className="bi bi-award-fill"></i>
                      </div>
                    </div>
                    
                    <div className="certificate-details">
                      <div className="certificate-header">
                        <h3 className="certificate-title" data-translate={`certificate_${activeTab}_title`}>
                          {activeCertificate.title}
                        </h3>
                        <p className="certificate-institution">
                          {activeCertificate.institution}
                        </p>
                      </div>
                      
                      <div className="certificate-skills">
                        <div className="skills-title">
                          <i className="bi bi-check-circle-fill"></i>
                          <span data-translate="certificate_skills_developed">Competências Desenvolvidas</span>
                        </div>
                        <div className="skills-tags">
                          {activeCertificate.skills.map((skill, index) => (
                            <span key={index} className={`skill-tag ${skill.toLowerCase().replace(/\s+/g, '-')}`}>
                              {skill}
                            </span>
                          ))}
                        </div>
                      </div>
                      
                      <div className="certificate-description">
                        <p data-translate={`certificate_${activeTab}_desc`}>
                          {activeCertificate.description}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Certificates;
