import React, { useEffect, useState } from 'react';
import { Icon } from '@iconify/react';

const TechStack = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
          }
        });
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById('testimonials');
    if (element) {
      observer.observe(element);
    }

    return () => {
      if (element) {
        observer.unobserve(element);
      }
    };
  }, []);

const technologies = [
  { component: <Icon icon="logos:javascript" width="48" />, name: 'JavaScript' },
  { component: <Icon icon="logos:html-5" width="48" />, name: 'HTML' },
  { component: <Icon icon="logos:php" width="48" />, name: 'PHP' },
  { component: <Icon icon="logos:react" width="48" />, name: 'React' },
  { component: <Icon icon="logos:mysql" width="48" />, name: 'MySQL' },
  { component: <Icon icon="logos:java" width="48" />, name: 'Java' },
  { component: <Icon icon="logos:angular-icon" width="48" />, name: 'Angular' },
  { component: <Icon icon="logos:css-3" width="48" />, name: 'CSS' }
];


  return (
    <section id="testimonials" className="testimonials section">
      <div className="container section-title" data-aos="fade-up">
        <h2 data-translate="tech_title">Stack Tecnol√≥gica</h2>
      </div>
      
      <div className="container" data-aos="fade-up" data-aos-delay="100">
        <div className="tech-slider-container">
          <div 
            className={`tech-slider-track ${isVisible ? 'animate' : ''}`}
            style={isVisible ? { animation: 'scroll 90s linear infinite' } : {}}
          >
            {technologies.map((tech, index) => (
              <div key={`first-${index}`} className="tech-slide">
                {tech.component ? (
                  <div className="tech-icon">
                    {tech.component}
                  </div>
                ) : (
                  <i className={tech.icon} style={{ color: tech.color }}></i>
                )}
                <h3>{tech.name}</h3>
              </div>
            ))}
            
            {technologies.map((tech, index) => (
              <div key={`second-${index}`} className="tech-slide">
                {tech.component ? (
                  <div className="tech-icon">
                    {tech.component}
                  </div>
                ) : (
                  <i className={tech.icon} style={{ color: tech.color }}></i>
                )}
                <h3>{tech.name}</h3>
              </div>
            ))}
          </div>
        </div>
      </div>
      
      <style jsx>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
        
        .tech-slider-track.animate {
          animation: scroll 90s linear infinite;
        }
      `}</style>
    </section>
  );
};

export default TechStack;
