import React, { useEffect, useState } from 'react';
import { Icon } from '@iconify/react';

const TechStack = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
          }
        });
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById('testimonials');
    if (element) {
      observer.observe(element);
    }

    return () => {
      if (element) {
        observer.unobserve(element);
      }
    };
  }, []);

  const technologies = [
    { icon: 'fab fa-js-square', name: 'JavaScript', color: '#f7df1e' },
    { icon: 'fab fa-html5', name: 'HTML', color: '#e34c26' },
    { icon: 'fab fa-php', name: 'PHP', color: '#8892be' },
    { icon: 'fab fa-react', name: 'React', color: '#61DAFB' },
    { icon: '', component: <Icon icon="logos:mysql" />, name: 'MySQL', color: '#00758f' },
    { icon: '', component: <Icon icon="logos:java" />, name: 'Java', color: '#f89820' },
    { icon: 'fab fa-angular', name: 'Angular', color: '#dd0031' },
    { icon: 'fab fa-css3-alt', name: 'CSS', color: '#264de4' }
  ];

  return (
    <section id="testimonials" className="testimonials section">
      <div className="container section-title" data-aos="fade-up">
        <h2 data-translate="tech_title">Stack Tecnol√≥gica</h2>
      </div>
      
      <div className="container" data-aos="fade-up" data-aos-delay="100">
        <div className="tech-slider-container">
          <div 
            className={`tech-slider-track ${isVisible ? 'animate' : ''}`}
            style={isVisible ? { animation: 'scroll 90s linear infinite' } : {}}
          >
            {technologies.map((tech, index) => (
              <div key={`first-${index}`} className="tech-slide">
                {tech.component ? (
                  <div className="tech-icon">
                    {tech.component}
                  </div>
                ) : (
                  <i className={tech.icon} style={{ color: tech.color }}></i>
                )}
                <h3>{tech.name}</h3>
              </div>
            ))}
            
            {technologies.map((tech, index) => (
              <div key={`second-${index}`} className="tech-slide">
                {tech.component ? (
                  <div className="tech-icon">
                    {tech.component}
                  </div>
                ) : (
                  <i className={tech.icon} style={{ color: tech.color }}></i>
                )}
                <h3>{tech.name}</h3>
              </div>
            ))}
          </div>
        </div>
      </div>
      
      <style jsx>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
        
        .tech-slider-track.animate {
          animation: scroll 90s linear infinite;
        }
      `}</style>
    </section>
  );
};

export default TechStack;
