  const filters = [
    { key: '*', label: 'filter_all' },
    { key: 'web', label: 'filter_websites' },
    { key: 'links', label: 'filter_links' },
    { key: 'motion', label: 'filter_tools' },
    { key: 'brand', label: 'filter_fun' }
  ];

  // Verificar se todos os projetos têm filtros válidos
  useEffect(() => {
    const validFilters = ['*', 'web', 'links', 'motion', 'brand'];
    projects.forEach(project => {
      if (!validFilters.includes(project.filter)) {
        console.warn(`Projeto "${project.title}" tem filtro inválido: ${project.filter}`);
      }
    });
  }, []);

  useEffect(() => {
    if (!portfolioContainer.current) return;

    // Inicializar Isotope com configurações otimizadas
    const iso = new Isotope(portfolioContainer.current, {
      itemSelector: '.portfolio-item',
      layoutMode: 'masonry',
      masonry: {
        columnWidth: '.portfolio-item',
        gutter: 30
      },
      getSortData: {
        id: '[data-id] parseInt',
      },
      sortBy: 'id',
      filter: activeFilter,
      transitionDuration: '0.6s',
      stagger: 30,
      resize: true,
      fitWidth: true
    });

    isotope.current = iso;

    // Aguardar imagens carregarem para layout correto
    const imgLoad = imagesLoaded(portfolioContainer.current);
    imgLoad.on('always', () => {
      setTimeout(() => {
        iso.layout();
      }, 100);
    });

    // Adicionar listener para redimensionamento
    const handleResize = () => {
      if (isotope.current) {
        setTimeout(() => {
          isotope.current.layout();
        }, 100);
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      if (isotope.current) {
        isotope.current.destroy();
      }
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Efeito para atualizar o filtro ativo
  useEffect(() => {
    if (isotope.current) {
      console.log(`Aplicando filtro: ${activeFilter}`);
      isotope.current.arrange({ 
        filter: activeFilter 
      });
    }
  }, [activeFilter]);

  const handleFilterClick = (filterKey) => {
    console.log(`Clicou no filtro: ${filterKey}`);
    setActiveFilter(filterKey);
  };

  // Contador de projetos por categoria para debug
  const countProjectsByFilter = (filter) => {
    if (filter === '*') return projects.length;
    return projects.filter(project => project.filter === filter).length;
  };

  const getStackBadgeClass = (stack) => {
    const classes = {
      'fullstack': 'stack-badge fullstack',
      'frontend-only': 'stack-badge frontend-only',
      'angular-badge': 'stack-badge angular-badge',
      'fullstack angular-badge': 'stack-badge angular-badge'
    };
    return classes[stack] || 'stack-badge frontend-only';
  };

  const getTagClass = (tag) => {
    const tagClasses = {
      'HTML': 'html-tag',
      'CSS': 'css-tag',
      'JavaScript': 'js-tag',
      'Java': 'java-tag',
      'MySQL': 'mysql-tag',
      'TypeScript': 'typescript-tag',
      'SCSS': 'css-tag',
      'PHP': 'php-tag',
      'Docker': 'docker-tag',
      'API': 'api-tag',
      'Canvas API': 'canvas-tag',
      'Date API': 'date-tag',
      'Web Audio API': 'audio-tag',
      'Charts': 'chart-tag',
      'Authentication': 'auth-tag',
      'H2-Database': 'mysql-tag'
    };
    return tagClasses[tag] || 'js-tag';
  };

  return (
    <section id="portfolio" className="portfolio section">
      <div className="container section-title" data-aos="fade-up">
        <h2 data-translate="portfolio_title">Meus Projetos</h2>
