// src/components/Header.jsx
import React, { useState, useEffect } from 'react';

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileNavActive, setMobileNavActive] = useState(false);

  useEffect(() => {
    console.log('Header montado');
    
    const handleScroll = () => {
      setScrolled(window.scrollY > 100);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Teste: verifique no console se as variáveis CSS estão disponíveis
  useEffect(() => {
    console.log('Variáveis CSS disponíveis:', {
      accentColor: getComputedStyle(document.documentElement).getPropertyValue('--accent-color'),
      headingColor: getComputedStyle(document.documentElement).getPropertyValue('--heading-color')
    });
  }, []);

  const navItems = [
    { href: '#hero', text: 'menu_about' },
    { href: '#metodologias', text: 'menu_methodologies' },
    { href: '#service-details', text: 'menu_certificates' },
    { href: '#services', text: 'menu_services' },
    { href: '#portfolio', text: 'menu_projects' },
    { href: '#form-section', text: 'menu_contact' },
  ];

  return (
    <header 
      id="header" 
      className={`header d-flex align-items-center fixed-top ${scrolled ? 'scrolled' : ''}`}
      style={{
        // TESTE VISUAL TEMPORÁRIO
        backgroundColor: scrolled ? 'rgba(255, 255, 255, 0.9)' : 'transparent',
        transition: 'all 0.5s',
        zIndex: 997,
        padding: '15px 0',
        border: '2px solid red' // Remova depois que verificar
      }}
    >
      <div className="container position-relative d-flex align-items-center justify-content-between">
        <a href="/" className="logo d-flex align-items-center me-auto me-xl-0">
          <h1 className="sitename">Matheus Abib</h1><span>.</span>
        </a>

        <nav id="navmenu" className={`navmenu ${mobileNavActive ? 'mobile-nav-active' : ''}`}>
          <ul>
            {navItems.map((item, index) => (
              <li key={index}>
                <a 
                  href={item.href} 
                  data-translate={item.text}
                  onClick={() => setMobileNavActive(false)}
                >
                  {item.href === '#hero' ? 'Sobre mim' : 
                   item.href === '#metodologias' ? 'Metodologias' :
                   item.href === '#service-details' ? 'Certificados' :
                   item.href === '#services' ? 'Serviços' :
                   item.href === '#portfolio' ? 'Projetos' : 'Contato'}
                </a>
              </li>
            ))}
            <li className="d-none d-xl-block">
              <button className="btn-translate" id="translateBtnDesktop" data-lang="pt">
                <i className="bi bi-translate"></i>
                <span data-translate="btn_translate">PT/EN</span>
              </button>
            </li>
          </ul>
          <i 
            className={`mobile-nav-toggle d-xl-none bi ${mobileNavActive ? 'bi-x' : 'bi-list'}`}
            onClick={() => setMobileNavActive(!mobileNavActive)}
          ></i>
        </nav>
      </div>
    </header>
  );
};

export default Header;
