import React from 'react';

const Methodologies = () => {
  const methodologies = [
    {
      number: "01",
      icon: "bi-gear",
      title: "Engenharia de Software",
      description: "Planejo e organizo cada etapa do projeto utilizando modelagem UML, metodologias ágeis, Kanban, controle de versões com Git e testes contínuos."
    },
    {
      number: "02",
      icon: "bi-diagram-3",
      title: "Análise de Dados",
      description: "Estruturo as informações de maneira clara e organizada, utilizando ferramentas como Power BI e Excel para gerar insights estratégicos."
    },
    {
      number: "03",
      icon: "bi-card-checklist",
      title: "Controle de Qualidade e Gestão de Produto",
      description: "Garanto que o produto atenda às necessidades exigidas, com testes, análise de requisitos e entrega de soluções de valor."
    },
    {
      number: "04",
      icon: "bi-person-lines-fill",
      title: "Comunicação e Colaboração",
      description: "Priorizo comunicação clara, escuta ativa e colaboração, mesmo remotamente, sempre agindo com ética e responsabilidade."
    }
  ];

  return (
    <section id="metodologias" className="how-we-work section">
      <div className="container section-title" data-aos="fade-up">
        <h2 data-translate="methodologies_title">Como eu trabalho</h2>
      </div>

      <div className="container" data-aos="fade-up" data-aos-delay="100">
        <div className="steps-5">
          <div className="process-container">
            {methodologies.map((method, index) => (
              <div 
                key={index} 
                className="process-item" 
                data-aos="fade-up" 
                data-aos-delay={300 + (index * 100)}
              >
                <div className="content">
                  <span className="step-number">{method.number}</span>
                  <div className="card-body">
                    <div className="step-icon">
                      <i className={`bi ${method.icon}`}></i>
                    </div>
                    <div className="step-content">
                      <h3>{method.title}</h3>
                      <p>{method.description}</p>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Methodologies;
