import React, { useState } from 'react';

const Certificates = () => {
  const [activeTab, setActiveTab] = useState('webdev');

  // Mapeamento de classes CSS para cada skill
  const skillClasses = {
    'CSS': 'css',
    'Bootstrap': 'bootstrap',
    'JavaScript': 'javascript',
    'HTML5': 'html',
    'AJAX': 'ajax',
    'Microsoft Power BI': 'angular', // Usando angular class para Power BI
    'Microsoft Excel': 'spreadsheet-management',
    'Dashboards': 'dashboards',
    'Data Analysis': 'data-analysis',
    'Spreadsheet Management': 'spreadsheet-management',
    'Database': 'ajax', // Usando ajax class para Database
    'SQL': 'mysql',
    'Git': 'git',
    'GitHub': 'github',
    'Logic': 'data-analysis', // Usando data-analysis class para Logic
    'AI Fundamentals': 'ai',
    'Supervised Learning': 'angular', // Usando angular class
    'Data Preprocessing': 'ajax', // Usando ajax class
    'Python': 'python',
    'Java': 'java',
    'Spring Boot': 'spring',
    'API Rest': 'php',
    'Lombok': 'angular' // Usando angular class
  };

  // Mapeamento de chaves de tradução para skills
  const skillTranslationKeys = {
    'Data Analysis': 'skill_data_analysis',
    'Dashboards': 'skill_dashboards',
    'Spreadsheet Management': 'skill_spreadsheet_management',
    'Database': 'skill_database',
    'Logic': 'skill_logic',
    'AI Fundamentals': 'skill_ai_fundamentals',
    'Supervised Learning': 'skill_supervised_learning',
    'Data Preprocessing': 'skill_data_preprocessing'
  };

  const certificates = [
    {
      id: 'webdev',
      title_key: 'certificate_webdev_title',
      institution: 'Coursera - Johns Hopkins University',
      image: '/assets/img/services/Web-Development.jpg',
      description_key: 'certificate_webdev_desc',
      skills: ['CSS', 'Bootstrap', 'JavaScript', 'HTML5', 'AJAX']
    },
    {
      id: 'powerbi',
      title_key: 'certificate_powerbi_title',
      institution: 'Data Science Academy',
      image: '/assets/img/services/Power-BI.jpg',
      description_key: 'certificate_powerbi_desc',
      skills: ['Microsoft Power BI', 'Microsoft Excel', 'Dashboards', 'Data Analysis', 'Spreadsheet Management']
    },
    {
      id: 'database',
      title_key: 'certificate_database_title',
      institution: 'IFSul - Plataforma Mundi',
      image: '/assets/img/services/Banco-de-Dados.jpg',
      description_key: 'certificate_database_desc',
      skills: ['Database', 'SQL', 'Spreadsheet Management', 'Data Analysis']
    },
    {
      id: 'logic',
      title_key: 'certificate_logic_title',
      institution: 'DIO - Digital Innovation One',
      image: '/assets/img/services/Logica-de-Programacao.jpg',
      description_key: 'certificate_logic_desc',
      skills: ['JavaScript', 'Git', 'GitHub', 'Logic']
    },
    {
      id: 'ai',
      title_key: 'certificate_ai_title',
      institution: 'Alura - Imersão IA',
      image: '/assets/img/services/IA.jpg',
      description_key: 'certificate_ai_desc',
      skills: ['AI Fundamentals', 'Supervised Learning', 'Data Preprocessing', 'Python']
    },
    {
      id: 'java',
      title_key: 'certificate_java_title',
      institution: 'Especialização em Java',
      image: '/assets/img/services/Java.png',
      description_key: 'certificate_java_desc',
      skills: ['Java', 'Spring Boot', 'API Rest', 'Database', 'Lombok']
    }
  ];

  const tabs = [
    { id: 'webdev', label_key: 'webdev_tab_title' },
    { id: 'powerbi', label_key: 'powerbi_tab_title' },
    { id: 'database', label_key: 'database_tab_title' },
    { id: 'logic', label_key: 'logic_tab_title' },
    { id: 'ai', label_key: 'ai_tab_title' },
    { id: 'java', label_key: 'java_tab_title' }
  ];

  const activeCertificate = certificates.find(cert => cert.id === activeTab);

  return (
    <section id="service-details" className="service-details section">
      <div className="container section-title" data-aos="fade-up">
        <h2 data-translate="menu_certificates">Certificados</h2>
      </div>
      
      <div className="container" data-aos="fade-up" data-aos-delay="100">
        <div className="certificates-wrapper">
          <div className="certificates-tabs-modern">
            <div className="certificates-nav">
              {tabs.map(tab => (
                <button
                  key={tab.id}
                  className={`certificate-tab-btn ${activeTab === tab.id ? 'active' : ''}`}
                  onClick={() => setActiveTab(tab.id)}
                  data-tab={tab.id}
                >
                  <span data-translate={tab.label_key}>
                    {tab.id === 'webdev' ? 'Web Development' :
                     tab.id === 'powerbi' ? 'Power BI' :
                     tab.id === 'database' ? 'Banco de Dados' :
                     tab.id === 'logic' ? 'Lógica de Programação' :
                     tab.id === 'ai' ? 'Inteligência Artificial' : 'Java'}
                  </span>
                </button>
              ))}
            </div>
            
            <div className="certificates-content">
              {activeCertificate && (
                <div className="certificate-tab active" id={`${activeTab}-tab`}>
                  <div className="certificate-card">
                    <div className="certificate-image">
                      <img src={activeCertificate.image} alt={`Certificado ${activeCertificate.title_key}`} />
                      <div className="certificate-badge">
                        <i className="bi bi-award-fill"></i>
                      </div>
                    </div>
                    
                    <div className="certificate-details">
                      <div className="certificate-header">
                        <h3 className="certificate-title" data-translate={activeCertificate.title_key}>
                          {activeCertificate.id === 'webdev' ? 'HTML, CSS & JavaScript' :
                           activeCertificate.id === 'powerbi' ? 'Microsoft Power BI' :
                           activeCertificate.id === 'database' ? 'Banco de Dados SQL' :
                           activeCertificate.id === 'logic' ? 'Lógica de Programação' :
                           activeCertificate.id === 'ai' ? 'Inteligência Artificial' : 'Java & Spring Boot'}
                        </h3>
                        <p className="certificate-institution">
                          {activeCertificate.institution}
                        </p>
                      </div>
                      
                      <div className="certificate-skills">
                        <div className="skills-title">
                          <i className="bi bi-check-circle-fill"></i>
                          <span data-translate="certificate_skills_developed">Competências Desenvolvidas</span>
                        </div>
                        <div className="skills-tags">
                          {activeCertificate.skills.map((skill, index) => {
                            const skillClass = skillClasses[skill] || skill.toLowerCase().replace(/\s+/g, '-');
                            const translationKey = skillTranslationKeys[skill];
                            
                            return (
                              <span key={index} className={`skill-tag ${skillClass}`}>
                                {translationKey ? (
                                  <span data-translate={translationKey}>{skill}</span>
                                ) : skill}
                              </span>
                            );
                          })}
                        </div>
                      </div>
                      
                          <div className="certificate-description">
                            <p data-translate={activeCertificate.description_key}>
                              {activeCertificate.id === 'webdev' ? '...' :
                              activeCertificate.id === 'powerbi' ? '...' :
                              activeCertificate.id === 'database' ? '...' :
                              activeCertificate.id === 'logic' ? '...' :
                              activeCertificate.id === 'ai' ? '...' : 
                              '...'}
                            </p>
                          </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Certificates;
