import React, { useState, useEffect } from 'react';
import { useTranslation } from '../hooks/useTranslation';

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileNavActive, setMobileNavActive] = useState(false);
  const { toggleLanguage } = useTranslation();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 100);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleNavClick = () => {
    setMobileNavActive(false);
    document.body.classList.remove('mobile-nav-active');
  };

  const toggleMobileNav = () => {
    setMobileNavActive(!mobileNavActive);
    if (!mobileNavActive) {
      document.body.classList.add('mobile-nav-active');
    } else {
      document.body.classList.remove('mobile-nav-active');
    }
  };

  const navItems = [
    { href: '#hero', text: 'menu_about', icon: 'bi-person' },
    { href: '#metodologias', text: 'menu_methodologies', icon: 'bi-diagram-3' },
    { href: '#service-details', text: 'menu_certificates', icon: 'bi-award' },
    { href: '#services', text: 'menu_services', icon: 'bi-briefcase' },
    { href: '#portfolio', text: 'menu_projects', icon: 'bi-folder' },
    { href: '#form-section', text: 'menu_contact', icon: 'bi-envelope' },
  ];

  return (
    <>
      <header 
        id="header" 
        className={`header d-flex align-items-center fixed-top ${scrolled ? 'scrolled' : ''}`}
      >      
       <div className="container position-relative d-flex align-items-center justify-content-between">
  
  {/* Logo à esquerda */}
  <a href="/" className="logo d-flex align-items-center me-auto me-xl-0" onClick={handleNavClick}>
    <h1 className="sitename">Matheus Abib</h1><span>.</span>
  </a>

  {/* Botão de idioma DESKTOP à direita */}
  <div className="d-none d-xl-block">
    <button 
      className="btn-translate" 
      id="translateBtnDesktop" 
      data-lang="pt"
      onClick={toggleLanguage}
    >
      <i className="bi bi-translate"></i>
      <span data-translate="btn_translate">PT/EN</span>
    </button>
  </div>

  {/* Hamburguer Menu à direita (antes do nav) */}
  <i 
    className={`mobile-nav-toggle d-xl-none bi ${mobileNavActive ? 'bi-x' : 'bi-list'}`}
    onClick={toggleMobileNav}
    aria-label="Toggle navigation"
  ></i>

  {/* Navmenu - este fica normalmente */}
  <nav id="navmenu" className={`navmenu ${mobileNavActive ? 'mobile-nav-active' : ''}`}>
            <ul>
              {navItems.map((item, index) => (
                <li key={index}>
                  <a 
                    href={item.href} 
                    data-translate={item.text}
                    onClick={handleNavClick}
                    className="d-flex align-items-center"
                  >
                    <i className={`${item.icon} me-2`}></i>
                    <span>{item.text}</span>
                  </a>
                </li>
              ))}
              <li className="d-block d-xl-none mt-3 pt-3 border-top">
                <button 
                  className="btn-translate w-100 d-flex justify-content-center align-items-center" 
                  onClick={() => {
                    toggleLanguage();
                    handleNavClick();
                  }}
                >
                  <i className="bi bi-translate me-2"></i>
                  <span data-translate="btn_translate">PT/EN</span>
                </button>
              </li>
            </ul>
          </nav>

          <div className="d-none d-xl-block">
            <button 
              className="btn-translate" 
              id="translateBtnDesktop" 
              data-lang="pt"
              onClick={toggleLanguage}
            >
              <i className="bi bi-translate"></i>
              <span data-translate="btn_translate">PT/EN</span>
            </button>
          </div>

          <i 
            className={`mobile-nav-toggle d-xl-none bi ${mobileNavActive ? 'bi-x' : 'bi-list'}`}
            onClick={toggleMobileNav}
            aria-label="Toggle navigation"
          ></i>
        </div>
      </header>

      {mobileNavActive && (
        <div 
          className="mobile-nav-overlay"
          onClick={handleNavClick}
        ></div>
      )}
    </>
  );
};

export default Header;
